import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import DeleteBtn from "../components/DeleteBtn";
import EditBtn from "../components/EditBtn";

class Notes extends Component {

  state = {
    notes: [],
    patient: []
  }

  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc


  componentDidMount() {
    console.log(this.props.match.params.id)
    fetch("http://localhost:3010/api/notes/" + this.props.match.params.id, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },


    }).then((res) => {
      res.json().then((data) => {
        this.setState({ notes: data })
        console.log(this.state);
      });
    });

    
    fetch("http://localhost:3010/api/patient/" + this.props.match.params.id, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },


    }).then((res) => {
      res.json().then((data) => {
        this.setState({ patient: data })
        console.log(this.state);

      });
    });

  };

  deletePatient = id => {
    fetch("http://localhost:3010/api/notes/" + id, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },

    })
        .then(res => this.componentDidMount())
        .catch(err => console.log(err));
};



editNote = id => {

  fetch("http://localhost:3010/api/notes/" + id, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      First_Name: this.state.First_Name,
      Last_Name: this.state.Last_Name,
      Date_Of_Birth: this.state.Date_Of_Birth,
      Address: this.state.Address,
      City: this.state.City,
      State: this.state.State, 
      Zip_Code: this.state.Zip_Code, 
      Email: this.state.Email, 
      Phone_Number: this.state.Phone_Number,
      orgID: this.state.orgID

    })

  })
  fetch("http://localhost:3010/api/notes/" + id, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },

  })
      .then(res => this.componentDidMount())
      .catch(err => console.log(err));
};

  render() {
    return (

      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>
                {this.state.patient.map(patients => (
                  <p key={patients}>{patients.First_Name} {patients.Last_Name}</p>
                ))}
              </h1>
            </Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">
            <article>
              <h1>Patient Notes</h1>

              <br></br>
              {this.state.notes.map((notes, id) => (
                <p key={id}>{notes.Note}
                
                <DeleteBtn onClick={() => { if (window.confirm('Are you sure you wish to delete this patient?')) this.deletePatient(notes.id)}} />
                <EditBtn onClick={() => this.editNote(notes.id)}/>
                </p>
              ))}

            </article>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/PatientList">‚Üê Back to Patients</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Notes;
